// Write a function that calls mlb api and returns the current standings for the AL East

async function getStandings() {
  const response = await fetch('https://statsapi.mlb.com/api/v1/standings?leagueId=103&season=2019&standingsTypes=regularSeason&hydrate=division,conference,sport,league');
  const data = await response.json();
  return data.records;
}

// Write a function that calls mlb API and returns the attendance for the 2019 season

async function getAttendance() {
    const response = await fetch('https://statsapi.mlb.com/api/v1/attendance?leagueId=103&season=2019');
    const data = await response.json();
    return data.attendance;
}

// Write a function that calls mlb API and returns team stats for the 2019 season
async function getTeamStats() {
    const response = await fetch('https://statsapi.mlb.com/api/v1/teams?season=2019&hydrate=teamStats');
    const data = await response.json();
    return data.teams;
}

// Write a function that calls mlb API and returns team stats for the 2019 season by group
async function getTeamStatsByGroup() {
    const response = await fetch('https://statsapi.mlb.com/api/v1/teams?season=2019&hydrate=teamStats(group=[hitting,pitching])');
    const data = await response.json();
    return data.teams;
}